$(function () {
    var l = abp.localization.getResource('CoreOracle');
    
    // Service proxies generated by ABP CLI (ensure namespace is correct)
    // Example: var countryService = aqt.coreOracle.countries.country;
    // Please verify the actual generated proxy namespace
    var countryService = aqt.coreOracle.application.countries.country; // Adjusted based on likely structure
    
    var createModal = new abp.ModalManager({
        viewUrl: abp.appPath + 'Countries/CreateModal',
        // scriptUrl: '/Pages/Countries/createModal.js', // Optional: if CreateModal has specific JS
        modalClass: 'countryCreateModal' // Optional: CSS class for modal
    });
    
    var editModal = new abp.ModalManager({
        viewUrl: abp.appPath + 'Countries/EditModal',
        // scriptUrl: '/Pages/Countries/editModal.js', // Optional: if EditModal has specific JS
        modalClass: 'countryEditModal' // Optional: CSS class for modal
    });

    var dataTable = $('#CountriesTable').DataTable(
        abp.libs.datatables.normalizeConfiguration({
            serverSide: true,
            paging: true,
            order: [[1, "asc"]], // Default sort by Code (index 1)
            searching: false, // Disable native search, use custom filter if needed
            scrollX: true,
            ajax: abp.libs.datatables.createAjax(countryService.getList), // Use service proxy
            columnDefs: [
                {
                    title: l('Actions'),
                    rowAction: {
                        items: [
                            {
                                text: l('Edit'),
                                icon: "fa fa-pencil-alt", // Optional icon
                                visible: abp.auth.isGranted('CoreOracle.Countries.Edit'), // Check Edit permission
                                action: function (data) {
                                    editModal.open({ id: data.record.id });
                                }
                            },
                            {
                                text: l('Delete'),
                                icon: "fa fa-trash", // Optional icon
                                visible: abp.auth.isGranted('CoreOracle.Countries.Delete'), // Check Delete permission
                                confirmMessage: function (data) {
                                    // Use localization with placeholder
                                    return l('AreYouSureToDelete', data.record.name || data.record.code);
                                },
                                action: function (data) {
                                    countryService.delete(data.record.id)
                                        .then(function () {
                                            abp.notify.success(l('SuccessfullyDeleted'));
                                            dataTable.ajax.reload(); // Reload table data
                                        })
                                        .catch(function (error) {
                                            // Attempt to display server-side validation/error messages
                                            if (error.message) {
                                                abp.notify.error(error.message);
                                            } else {
                                                abp.notify.error(l('Error')); // Generic error
                                            }
                                        });
                                }
                            }
                        ]
                    }
                },
                {
                    title: l('CountryCode'), // Use localized title
                    data: "code",
                    orderable: true // Allow sorting by Code
                },
                {
                    title: l('CountryName'), // Use localized title
                    data: "name",
                    orderable: true // Allow sorting by Name
                }
            ]
        })
    );

    // Refresh table on modal close/save
    createModal.onResult(function () {
        dataTable.ajax.reload();
        abp.notify.success(l('SuccessfullySaved')); // Notify on successful creation
    });

    editModal.onResult(function () {
        dataTable.ajax.reload();
        abp.notify.success(l('SuccessfullySaved')); // Notify on successful update
    });

    // Handle 'New Country' button click
    $('#NewCountryButton').click(function (e) {
        e.preventDefault();
        createModal.open();
    });
    
    // Optional: Add filter functionality if needed
    // Example for a text filter input with ID 'CountryFilter'
    /*
    $('#SearchButton').click(function (e) {
        e.preventDefault();
        dataTable.ajax.reload(); 
    });
    
    // Adjust createAjax to include filter data
    abp.libs.datatables.createAjax(countryService.getList, function() {
        return { filter: $('#CountryFilter').val() }; 
    });
    */

}); 